// Encodes the file
void encode(FILE *input, FILE *encodedOutput){
  char bytes[16384]; // TODO How big should this be?
  bytes[0] = '\0';
  int i, numBits = 0;
  char x;

  while((x = fgetc(input))!= EOF){ // Loop through each char in input file
          strcat(bytes, indexedCodes[(int)x]); // Add the code to bytes[]
          numBits += strlen(indexedCodes[(int)x]);
  }
 
 // Add padding - 0101010
 switch(numBits % 8) {
   case 7:
     strcat(bytes, "0");
     numBits += 1;
   case 6:
     strcat(bytes, "01"); 
     numBits += 2;
   case 5:
     strcat(bytes, "010"); 
     numBits += 3;
   case 4:
     strcat(bytes, "0101");
     numBits += 4;
   case 3:
     strcat(bytes, "01010"); 
     numBits += 5;
   case 2:
     strcat(bytes, "010101"); 
     numBits += 6;
   case 1:
     strcat(bytes, "0101010");
     numBits += 7;
 }
 
  printf("Binary with Padding:\n%s\n", bytes);
 
  char sub[9];
 
 // Write to encodedOutput file 1 byte at a time
  for(i = 0; i < (numBits / 8); i++) {
    strncpy(sub, bytes + (i*8), 8);
    sub[8] = '\0';
    long l = strtol(sub, 0, 2);
    unsigned char b = l;
    fwrite(&b, 1, 1, encodedOutput);
  }
}

void decode(FILE *input, FILE *output){
	char x;
	int i;
 
  char binaryString[5096]; // TODO How big should this be?
  binaryString[0] = '\0';
  int binarySize = 0;
	
	while ((x = fgetc(input))!= EOF){
    for (i = 7; i >= 0; --i) // Take char and shift to read each bit
    {
        binaryString[binarySize] = ((x & (1 << i)) ? '1' : '0' ); // Write each bit individually
        binarySize++;
    }
   }
   binaryString[binarySize] = '\0';

   int pos = 1;
   for(i = 0; i < strlen(binaryString); i++) { // Loop through binaryString character by character until '\0' reached   
      if(binaryString[i] == '0'){
      	pos = 2*pos;
      } else if(binaryString[i] == '1'){
      	pos = 2*pos + 1;
      } 
      printf("pos: %d\n",pos );
     printf("letter: %c\n",lettersArray[pos] );
      if(lettersArray[pos]){ // if the code corresponds to a letter, add it to decodedOutput file and reset position
  		   putc(lettersArray[pos], output);
       	 pos = 1;
      }
   }
  //printf("Decoded binary without padding: \n%s\n", binaryString);
}

int main(void){
	char* filename = "text.txt";
	FILE *input, *encodedOutput, *decodedOutput;
	input = fopen(filename, "r");
	encodedOutput = fopen("encodedOutput.txt","w");

	printf("Encoding:\n");
	encode(input, encodedOutput);
	fclose(encodedOutput);
 
  printf("\nDecoding: \n");
	encodedOutput = fopen("encodedOutput.txt", "r");
  decodedOutput = fopen("decodedOutput.txt","w");
	decode(encodedOutput, decodedOutput);
 	fclose(input);
 	fclose(encodedOutput);
  fclose(decodedOutput);
	return 0;
}